import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps } from 'react-router-dom';
import UserForm from '../components/user-form';
import User from '../models/user';
import UserService from '../services/user-service';
import formatName from '../helpers/format-name';
 
type Params = { id: string };
  
const UserEdit: FunctionComponent<RouteComponentProps<Params>> = ({ match }) => {
    
  const [user, setUser] = useState<User|null>(null);
  
  useEffect(() => {
    UserService.getUser(+match.params.id).then(user =>setUser(user));
  }, [match.params.id]);
    
  return (
    <div>
      { user ? (
        <div className="row">
            <h2 className="header center tokyo-title">
              <span>Ã‰diter le profil de </span>
              <br />
              <span>{ formatName(user.firstname) }</span> <span>{ formatName(user.lastname) }</span>
            </h2>
            <UserForm user={user}></UserForm>
        </div>
      ) : (
        <h4 className="center">NO user to display !</h4>
      )}
    </div>
  );
}
  
export default UserEdit;
