import React, { FunctionComponent, useState, useEffect } from 'react';
import User from '../models/user';
// import USERS from '../models/mock-users'; on ne s'en sert plus puisqu'on fait appel à UserService
import UserCard from '../components/user-card';
// import  useUsers  from '../hooks/user-hook'; on ne s'en sert plus 
import UserService from '../services/user-service';
  
const UserList: FunctionComponent = () => {

   const [users, setUsers] = useState<User[]>([]);
  
  useEffect(() => {
      UserService.getUsers().then(users => setUsers(users));
  }, []);
  
  return (       
    <div className="container"> 

        <div className="row"> 

            <h1 className="center tokyo-title">Users index</h1>

            {users.map(user => (
                <UserCard key={user.id} user={user} />
            ))}

        </div>
    </div>
    
  );
}
  
export default UserList;
