import React, { FunctionComponent, useState, useEffect } from 'react';
import Pokemon from '../models/pokemon';
// import POKEMONS from '../models/mock-pokemon'; on ne s'en sert plus puisqu'on fait appel à PokemonService
import PokemonCard from '../components/pokemon-card';
// import  usePokemons  from '../hooks/pokemon-hook'; on ne s'en sert plus puisqu'on fait appel à PokemonService
import PokemonService from '../services/pokemon-service';
  
const PokemonList: FunctionComponent = () => {

   const [pokemons, setPokemons] = useState<Pokemon[]>([]);
  
  useEffect(() => {
    // fetch('http://localhost:3001/pokemons')
    // .then(response => response.json())
    // .then((pokemons) => {
    //   setPokemons(pokemons)
    // });
      //   setPokemons(POKEMONS);
      PokemonService.getPokemons().then(pokemons => setPokemons(pokemons));
  }, []);

  // const pokemons = usePokemons();
  
  return (       
    <div className="container"> 

        <div className="row"> 

            <h1 className="center">Pokédex</h1>

            {pokemons.map(pokemon => (
                <PokemonCard key={pokemon.id} pokemon={pokemon} />
            ))}

        </div>
    </div>
    
  );
}
  
export default PokemonList;
