import React, { FunctionComponent, useEffect, useState } from 'react';
import Pokemon from './models/pokemon';
import POKEMONS from './models/mock-pokemon';
  
// const App: React.FC = () => {
const App: FunctionComponent = () =>
{
    // const name: String = 'React';
    // const [name, setname] = useState<String>('React');
    //ma version :
    // const [pokemons] = useState<Array<Pokemon>>(POKEMONS);
    const [pokemons, setPokemons] = useState<Pokemon[]>([]);

    useEffect(()=>{
        setPokemons(POKEMONS);
    }, []
    );

    const showPokemonCount = () => {
        console.log(pokemons.length);
    }

    // passer un paramètre depuis l'interface - 
       //- gestionnaire d'événement : 
    // const showPokemonName = (p) => {
    //     console.log(p);
    // }

    // passer une paramètre et un event depuis l'interface
       //- gestionnaire d'événement : 
    // const addPokemon = (name, e) => {
    //     if (e.nativeEvent.which === 1 ) { console.log(`Le pokemon ${name} a été ajouté avec le clic gauche`)}
    //     else if (e.nativeEvent.which === 3) { console.log(`Le pokemon ${name} a été ajouté avec le clic droit.`)};
    // };

    // conditionner un affichage
    const [age, setAge] = useState<number>(22);
    //Conditionner un affichage


    return (
    <div className="container">

        <div className="row">

            <div className="col 12">

                <h1 className="center">Pokedex</h1>

                {/* <p onClick={ showPokemonCount}>Clique et Affiche le nombre de pokemons en console</p> */}

                {/* <p onClick={showPokemonName("Carapuce")}>Clique et affiche le nom Carapuce en console</p> */}
                {/* <button onClick= { (e) => addPokemon(name, e) }>Ajouter un pokemon</button> */}

            {/* conditionner un affichage avec une simple condition équivalent if
                <div>
                    { 
                        age> 18 &&
                        <p>Vous êtes majeur</p>
                    }
                </div> */}

            {/* conditionner un affichage avec un opérateur ternaire ?  équivalent if else
                <div>
                    { age > 18 ? (Vouso etes majeur) : (Vous etes mineur) }
                </div>  */}
                <br />
                <h2 className="center">Liste des profils </h2>

                    { pokemons.map( ({id, name, picture, created}) => ( 
                        <div className="col l6 m4" key ={id} >
                            <div className="card horizontal">
                                <div className="card-image">
                                    <img src={ picture } alt={name} className="circle"/>
                                </div>
                                <div className="card-stacked">
                                    <div className="card-content">
                                        <h6>{ name } </h6>
                                        <p>created on {created.toString()} </p>
                                        <a href="#!" className="secondary-content"><i className="material-icons">grade</i></a>
                                    </div>
                                </div>                                    
                           </div>                         
                        </div> 
                    ))}           
                

                {/* <ul className="collection">
                    { pokemons.map( ({name}) => (
                        <li key = {name } className="collection-item avatar"><img src="https://assets.pokemon.com/assets/cms2/img/pokedex/detail/001.png" alt="image du pokemon" className="circle"><span className="title">{ name }</span></img> </li>
                    ) )}
                    
                </ul>   */}

            </div>

        </div>

    </div>
    )

}
  
export default App;

